import React, { useEffect, useState } from "react";

const items = [
  {
    name: "Italian-Style Chicken Meatball ",
    price: "$8.45",
    description:
      "Lasagna - a layered pasta with meat sauce, cheese & vegetables ",
    image:
      "https://images.unsplash.com/photo-1628157588553-5eeea00af15c?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1160&q=80",
  },
  {
    name: "Seafood Stuffed Alaskan Salmon",
    price: "$8.45",
    description:
      "Osso Buco - a slow-cooked veal shank served with a flavorful sauce",
    image:
      "https://images.unsplash.com/photo-1639149888905-fb39731f2e6c?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=928&q=80",
  },
  {
    name: "Seafood Stuffed Alaskan Salmon",
    price: "$8.45",
    description:
      "Osso Buco - a slow-cooked veal shank served with a flavorful sauce",
    image:
      "https://images.unsplash.com/photo-1639149888905-fb39731f2e6c?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=928&q=80",
  },
  {
    name: "Seafood Stuffed Alaskan Salmon",
    price: "$8.45",
    description:
      "Osso Buco - a slow-cooked veal shank served with a flavorful sauce",
    image:
      "https://images.unsplash.com/photo-1639149888905-fb39731f2e6c?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=928&q=80",
  },
  {
    name: "Seafood Stuffed Alaskan Salmon",
    price: "$8.45",
    description:
      "Osso Buco - a slow-cooked veal shank served with a flavorful sauce",
    image:
      "https://images.unsplash.com/photo-1639149888905-fb39731f2e6c?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=928&q=80",
  },
];

export function Products() {

  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [category,setCategory]= useState('Seafood');
  const [dishes, setDishes] = useState([]);
  const [load, setLoad] = useState(true);
  const [errormeal, setErrormeal] = useState(null);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch('https://www.themealdb.com/api/json/v1/1/list.php?c=list');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setCategories(data.meals);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchCategories();
  }, []);

  



  

  useEffect(() => {
    const fetchDishes = async () => {
      try {
        const response = await fetch(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${category}`);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setDishes(data.meals);
      } catch (errormeal) {
        setErrormeal(errormeal);
      } finally {
        setLoad(false);
      }
    };

    fetchDishes();
  }, [category]);



  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  if (load) {
    return <div>Loading Meals...</div>;
  }

  if (errormeal) {
    return <div>Error: {errormeal.message}</div>;
  }


  
  




  return (
    <>
      <div className="flex flex-col items-center space-y-4">
        <div className="text-center">
          <h2 className="text-lg mt-6 font-semibold">Shop By Category</h2>
          <h1 className="mt-1 text-xl font-bold text-gray-700">
            Top Category of Organic Food
          </h1>
        </div>
        <div>
          <div className="flex space-x-4">
          
            {categories.map((category, index) => (
              
              <button key={index}
              type="button"
              // onClick={setCategory(category.strCategory)}
              className="rounded-md bg-black px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-black/80 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-black"
            >
            {category.strCategory}
            </button>
            ))}
           



          </div>
        </div>
      </div>

      
    </>
  );
}
